import React, { Component } from 'react';
import { connect } from 'react-redux';
import { weatherData } from '../actions';

class ListView extends Component {
  componentDidUpdate() {
    if (this.props.searchedItem.length && this.props.weather.length === 0) {
      this.props.weatherData();
    }
  }

  componentDidMount() {
    this.unixToStandardConverter(1402565420);
  }

  //1580596920

  unixToStandardConverter(unixTime) {
    // UNIX Time to [DayOfWeek, Month, Day, Y, Military Time, Timezone ]
    let unixTimeConverter = new Date(unixTime * 1000).toString().split(' ');

    // Extract Time From unixTimeConverter variable
    let militaryTime = unixTimeConverter[4].split(':');

    // Remove the 0s from the end of the military time
    let militaryTimeShortened = `${militaryTime[0]}:${militaryTime[1]} `;

    // Check if the time is a.m. or p.m.
    // If a.m., return `military time + a.m`
    // If p.m., return `(military time - 12) + p.m.`

    if (Number(`${militaryTimeShortened[0]}${militaryTimeShortened[1]}`) < 12) {
      return `${militaryTimeShortened}am`;
    } else {
      let standardTime =
        Number(militaryTimeShortened[0] + militaryTimeShortened[1]) - 12;

      standardTime = `${standardTime}:${militaryTimeShortened[3]}${militaryTimeShortened[4]}`;
      return `${standardTime}pm`;
    }
  }

  renderInput() {
    if (this.props.weather.length === 0) {
      return <div>Hi there</div>;
    }

    return (
      <div>
        <h3>Location: {this.props.locationName}</h3>
        <h3>Temperature: {this.props.weather.data.currently.temperature}</h3>
        <h3>
          Feels Like: {this.props.weather.data.currently.apparentTemperature}
        </h3>
        <h3>Sunset Time: {this.props.weather.data.daily.data[0].sunsetTime}</h3>
        <h3>
          Precipitation Probability:
          {this.props.weather.data.currently.precipProbability}
        </h3>
        <h3>-------------------</h3>

        <h2>Chance of rain</h2>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[2].time
          )}
        </h3>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[4].time
          )}
        </h3>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[6].time
          )}
        </h3>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[8].time
          )}
        </h3>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[10].time
          )}
        </h3>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[12].time
          )}
        </h3>
      </div>
    );
  }

  render() {
    return <div className="ui container">{this.renderInput()}</div>;
  }
}

const mapStateToProps = state => {
  return {
    searchedItem: state.searchPhrase,
    weather: state.weather,
    locationName: state.clickedSearch
  };
};

export default connect(mapStateToProps, { weatherData })(ListView);
