import React from 'react';
import ReactAnimatedWeather from 'react-animated-weather';

const typeOfIcon = icon => {
  switch (icon.toUpperCase()) {
    case 'CLEAR-DAY':
      return 'CLEAR_DAY';
    case 'CLEAR-NIGHT':
      return 'CLEAR_NIGHT';
    case 'PARTLY-CLOUDY-DAY':
      return 'PARTLY_CLOUDY_DAY';
    case 'PARTLY-CLOUDY-NIGHT':
      return 'PARTLY_CLOUDY_NIGHT';
    case 'CLOUDY':
      return 'CLOUDY';
    case 'RAIN':
      return 'RAIN';
    case 'SLEET':
      return 'SLEET';
    case 'SNOW':
      return 'SNOW';
    case 'WIND':
      return 'WIND';
    case 'FOG':
      return 'FOG';
    default:
      break;
  }
};

const WeatherIcon = props => {
  if (!props.icon) {
    return `Loading...`;
  }

  return (
    <ReactAnimatedWeather
      icon={typeOfIcon(props.icon).icon}
      // color={typeOfIcon(props.icon).color}
    />
  );
};

export default WeatherIcon;
