import { SUBMITTED_SEARCH } from './types';
import mapBox from '../apis/mapBox';
// import darkSky from '../apis/darkSky';

// export const searchPhrase = submittedSearch => {
//   return { type: SUBMITTED_SEARCH, payload: submittedSearch };
// };

// export const accessToken = `pk.eyJ1IjoidmlubnljYW5jb2RlIiwiYSI6ImNrNjBhN2I4cDA1eTIza3NjMDhmc3prdnEifQ._6AKRg7gimHhIbM5pfBTXA`;

export const searchPhrase = search_text => async (dispatch, getState) => {
  const response = await mapBox.get(`/${search_text}.json?access_token=`);

  dispatch({ type: SUBMITTED_SEARCH, payload: response });
};
