import React, { Component } from 'react';
import { connect } from 'react-redux';

class ListView extends Component {
  componentDidUpdate() {
    console.log(getState());
  }
  renderInput() {
    if (this.props.searchedItem.length === 0) {
      return <div>Hi there</div>;
    }

    return (
      <div>
        <h3>Lat: {this.props.searchedItem[0]}</h3>
        <h3>Long: {this.props.searchedItem[1]} </h3>
      </div>
    );
  }

  render() {
    console.log(this.props.searchedItem);

    return <div className="ui container">{this.renderInput()}</div>;
  }
}

const mapStateToProps = state => {
  return { searchedItem: state.searchPhrase };
};

export default connect(mapStateToProps)(ListView);
