import { SUBMITTED_SEARCH } from './types';
import mapBox, { accessToken } from '../apis/mapBox';
// import darkSky from '../apis/darkSky';

// export const searchPhrase = submittedSearch => {
//   return { type: SUBMITTED_SEARCH, payload: submittedSearch };
// };

export const searchPhrase = submittedSearch => async (dispatch, getState) => {
  const response = await mapBox.get(
    `/${submittedSearch}.json?access_token=${accessToken}`
  );

  dispatch({
    type: SUBMITTED_SEARCH,
    payload: response.data.features[0].geometry.coordinates
  });
};

export const weatherData = (
  latitude,
  longitude,
  exclude = 'alerts, flags'
) => async (dispatch, getState) => {};
