import Axios from 'axios';

const accessToken = `pk.eyJ1IjoidmlubnljYW5jb2RlIiwiYSI6ImNrNjA5czV6YTA1dnQzbG52ZzN1bng3NGYifQ.Ui4oQoqdu2UCoOcrQtd8fw`;

export default Axios.create({
  baseURL: `https://api.mapbox.com/`,
  params: {
    access_token: `${accessToken}`
  }
});
