import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchHighlightedCity } from '../actions';

class HighlightedCities extends Component {
  componentDidMount() {
    this.props.fetchHighlightedCity();
  }

  renderRandomCities() {
    if (!this.props.highlightedCities) {
      return <div>Loading...</div>;
    }

    let randomCities = [];
    for (let i = 0; randomCities.length < 3; i++) {
      randomCities.push(
        this.props.highlightedCities[Math.floor(Math.random() * 39)]
      );
      i++;
    }

    return randomCities.map(city => (
      <div className="column">
        <div style={{ width: '30vw', gap: '1rem' }} className="ui fluid card">
          <div className="image">
            <img
              style={{ height: '400px', width: '100%' }}
              src={city.picture}
            />
          </div>
          <div className="content">
            <a className="header">{city.cityName}</a>
          </div>
        </div>
      </div>
    ));
  }

  render() {
    return (
      <div className="ui four column grid">{this.renderRandomCities()}</div>
    );
  }
}

const mapStateToProps = state => {
  return { highlightedCities: state.fetchCities };
};

export default connect(mapStateToProps, { fetchHighlightedCity })(
  HighlightedCities
);
