  }

  renderInput() {
    if (Object.keys(this.props.weather).length === 0) {
      return 'Daily View';
    }

    // let sevenDayData = this.props.weatherDailyData.daily.shift;
    return;
  }

  render() {
    let sevenDayData = this.props.weatherDailyData.daily.data;
    sevenDayData = sevenDayData.slice(1);
    return <div>Daily View</div>;
