import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchHighlightedCity } from '../actions';

class HighlightedCities extends Component {
  componentDidMount() {
    this.props.fetchHighlightedCity();
  }

  renderRandomCities() {
    if (!this.props.highlightedCities) {
      return <div>Loading...</div>;
    }

    let randomCities = [];
    for (let i = 0; randomCities.length < 4; i++) {
      randomCities.push(
        this.props.highlightedCities[Math.floor(Math.random() * 39)]
      );
      i++;
    }

    // <div>
    //     <h3>{city.cityName}</h3>
    //     <h4>{city.picture}</h4>
    //     <br />
    //   </div>

    return randomCities.map(city => (
      <div class="column">
        <div class="ui fluid card">
          <div class="image">
            <img src="/images/avatar/large/daniel.jpg" />
          </div>
          <div class="content">
            <a class="header">Daniel Louise</a>
          </div>
        </div>
      </div>
    ));
  }

  render() {
    return <div class="ui three column grid">{this.renderRandomCities()}</div>;
  }
}

const mapStateToProps = state => {
  return { highlightedCities: state.fetchCities };
};

export default connect(mapStateToProps, { fetchHighlightedCity })(
  HighlightedCities
);
