import React, { Component } from 'react';
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
// import { geocodeByPlaceId } from 'react-google-places-autocomplete';
// If you want to use the provided css
import 'react-google-places-autocomplete/dist/assets/index.css';
import { connect } from 'react-redux';
import { searchPhrase } from '../actions';

class PlacesAutoComplete extends Component {
  renderError = ({ error, touched }) => {
    if (error && touched) {
      return (
        <div className="ui error message">
          <div className="header">{error}</div>
        </div>
      );
    }
  };

  renderButtonColor = () => {
    // if (error && touched) {
    //   return <button className="ui button red">Search</button>;
    // }

    return (
      <button onSelect={console.log('hi')} className="ui button green">
        Search
      </button>
    );
  };
  render() {
    return (
      <div className="ui container">
        <div className="ui fluid action input">
          <GooglePlacesAutocomplete
            autocompletionRequest={{ types: ['(regions)'] }}
            onSelect={({ description }) => this.props.searchPhrase(description)}
          />
          {this.renderButtonColor()}
        </div>
      </div>
    );
  }
}
export default connect(null, { searchPhrase })(PlacesAutoComplete);
