import React, { Component } from 'react';
import { connect } from 'react-redux';
import { searchPhrase } from '../actions';

class SearchBar extends Component {
  state = { term: '' };

  componentDidMount() {
    console.log(this.props);
  }

  componentDidUpdate() {
    console.log(this.props);
  }

  onSubmit() {
    this.props.searchPhrase(this.state.term);
  }

  render() {
    return (
      <div className="ui container">
        <div className="ui fluid icon input">
          <input
            onChange={e => this.setState({ term: e.target.value })}
            onSubmit={this.onSubmit(this.state.term)}
            value={this.state.term}
            type="text"
            placeholder="Search..."
          />
          <i className="search icon"></i>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { state: state.searchPhrase };
};

export default connect(mapStateToProps, { searchPhrase })(SearchBar);
