import React, { Component } from 'react';
import { connect } from 'react-redux';
import { weatherData } from '../actions';
import WeatherIcon from './WeatherIcon';
import { unixToDay, unixToStandardConverter, decimalToPercent } from './Util';

class TodaysView extends Component {
  componentDidUpdate(prevState) {
    if (this.props.searchedItem.length !== 0) {
      if (this.props.weather === prevState.weather) {
        this.props.weatherData();
      }
    }
  }

  renderInput() {
    if (Object.keys(this.props.weather).length === 0) {
      return <div>Hi there</div>;
    }

    return (
      <div>
        <h3>
          Today:{' '}
          {this.unixToDay(this.props.weather.data.currently.time, 'Date')}
        </h3>
        <h3>Location: {this.props.locationName}</h3>
        <h3>
          Temperature:{' '}
          {Math.round(this.props.weather.data.currently.temperature)}° F
        </h3>
        <h3>
          {/* Icon: <WeatherIcon icon={this.props.weather.data.currently.icon} /> */}
        </h3>
        <h3>
          Feels Like{' '}
          {Math.round(this.props.weather.data.currently.apparentTemperature)}
        </h3>
        <h3>
          Sunset Time:{' '}
          {this.unixToStandardConverter(
            this.props.weather.data.daily.data[0].sunsetTime
          )}
        </h3>

        <h3>-------------------</h3>

        <h2>Chance of rain</h2>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[2].time
          )}
          :{' '}
          {this.decimalToPercent(
            this.props.weather.data.hourly.data[2].precipProbability
          )}
        </h3>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[4].time
          )}
          :
          {this.decimalToPercent(
            this.props.weather.data.hourly.data[4].precipProbability
          )}
        </h3>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[6].time
          )}
          :
          {this.decimalToPercent(
            this.props.weather.data.hourly.data[6].precipProbability
          )}
        </h3>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[8].time
          )}
          :
          {this.decimalToPercent(
            this.props.weather.data.hourly.data[8].precipProbability
          )}
        </h3>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[10].time
          )}
          :
          {this.decimalToPercent(
            this.props.weather.data.hourly.data[10].precipProbability
          )}
        </h3>
        <h3>
          {this.unixToStandardConverter(
            this.props.weather.data.hourly.data[12].time
          )}
          :
          {this.decimalToPercent(
            this.props.weather.data.hourly.data[12].precipProbability
          )}
        </h3>
      </div>
    );
  }

  render() {
    return <div className="ui container">{this.renderInput()}</div>;
  }
}

const mapStateToProps = state => {
  return {
    searchedItem: state.searchPhrase,
    weather: state.weather,
    locationName: state.clickedSearch
  };
};

export default connect(mapStateToProps, { weatherData })(TodaysView);
