import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchHighlightedCity } from '../actions';

class HighlightedCities extends Component {
  componentDidMount() {
    this.props.fetchHighlightedCity();
  }

  renderRandomCities() {
    if (!this.props.highlightedCities) {
      return <div>Loading...</div>;
    }

    let randomCities = [];
    for (let i = 0; i < 4; i++) {
      randomCities.push(
        this.props.highlightedCities[Math.floor(Math.random() * 39)]
      );
      i++;
    }

    console.log(randomCities);

    return randomCities.map(city => (
      <div>
        <h3>{city.cityName}</h3>
        <h4>{city.picture}</h4>
        <br />
      </div>
    ));
  }

  render() {
    return <div>{this.renderRandomCities()}</div>;
  }
}

const mapStateToProps = state => {
  return { highlightedCities: state.fetchCities };
};

export default connect(mapStateToProps, { fetchHighlightedCity })(
  HighlightedCities
);
