    t /= 3000;

    var a = cw * 0.16,
      b = s * 0.75,
      u = t * TAU * 0.7,
      ux = Math.cos(u) * b,
      uy = Math.sin(u) * b,
      v = u + TAU / 3,
      vx = Math.cos(v) * b,
      vy = Math.sin(v) * b,
      w = u + (TAU * 2) / 3,
      wx = Math.cos(w) * b,
      wy = Math.sin(w) * b,
      i,
      p,
      x,
      y;

    ctx.strokeStyle = color;
    ctx.lineWidth = s * 0.5;
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';

    for (i = 4; i--; ) {
      p = (t + i / 4) % 1;
