import React, { Component } from 'react';
import { connect } from 'react-redux';
import { weatherData } from '../actions';

class ListView extends Component {
  componentDidUpdate() {
    if (this.props.searchedItem.length && this.props.weather.length === 0) {
      this.props.weatherData();
    }
  }

  renderInput() {
    if (this.props.weather.length === 0) {
      return <div>Hi there</div>;
    }

    return (
      <div>
        <h3>Location:</h3> <h2>{this.props.locationName}</h2>
        <h3>Temperature: </h3>{' '}
        <h2>{this.props.weather.data.currently.temperature}</h2>
        <h3>Feels Like:</h3>
        <h2>{this.props.weather.data.currently.apparentTemperature}</h2>
        <h3>Sunset Time:</h3>
        <h2>{this.props.weather.data.daily.data[0].sunsetTime}</h2>
        <h3>Precipitation Probability:</h3>
        <h2>{this.props.weather.data.currently.precipProbability}</h2>
      </div>
    );
  }

  render() {
    return <div className="ui container">{this.renderInput()}</div>;
  }
}

const mapStateToProps = state => {
  return {
    searchedItem: state.searchPhrase,
    weather: state.weather,
    locationName: state.clickedSearch
  };
};

export default connect(mapStateToProps, { weatherData })(ListView);
