import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchHighlightedCity } from '../actions';

class HighlightedCities extends Component {
  componentDidMount() {
    this.props.fetchHighlightedCity();
  }
  componentDidUpdate() {
    console.log(this.props.highlightedCities);
    console.log(this.props.highlightedCities[Math.random(39) * Math.floor()]);
  }

  renderRandomCities() {
    if (this.props.highlightedCities.length === 0) {
      return <div>Loading...</div>;
    }

    let randomCities = [];
    for (let i; i < 4; i++) {
      randomCities.push(
        this.props.highlightedCities[Math.random(39) * Math.floor()]
      );
    }
  }

  render() {
    return <div>HighlightedCities</div>;
  }
}

const mapStateToProps = state => {
  return { highlightedCities: state.fetchCities };
};

export default connect(mapStateToProps, { fetchHighlightedCity })(
  HighlightedCities
);
